#include <iostream>
#include <string>
#include <map>
#include <algorithm>

using namespace std;

int main(){
		int n, biggest;
		string cs, c;
		int ci;
		map<string, int> rank;
		cin >> n;
		while(n != 0){
			biggest = 0;
			rank.clear();
			
			while(n--){
				c = "";
				for(int i = 0; i < 5; i++){
					cin >> ci;
					c[ci-100] = '1';
				}
				rank[c]++;
				biggest = max(biggest, rank[c]);
			}
			int amt = 0;
			for(map<string,int>::const_iterator i = rank.begin(); i != rank.end(); ++i){
				if(i->second == biggest)
					amt+= i->second;
			}
			cout << amt << '\n';
			cin >> n;
		}
}
